    public String[] words=lorem.split("\\s+");
    public String[] sentences=lorem.split("\\.");



    @Override
    public String generateParagraphs(int number) {
        StringBuilder l = new StringBuilder();
        for (int i = 0; i < number; i++) {
            for (int j =50*i; j <=50*i+49; j++) {
                if (j > 0 && (j % 49 == 0)) {
                    l.append("\n");
                }
                l.append(lorem.charAt(j));
            }
        }
        String l1 = l.substring(0,l.length()-number);
        return l1.toString();
    }

    @Override
    public String generateSentences(int number) {
        StringBuilder l = new StringBuilder();
        for(int i=0; i<number; i++){
            l.append(sentences[i] + ". ");
        }
        return l.toString();
    }

    @Override
    public String generateWords(int number) {
        StringBuilder l = new StringBuilder();
        for(int i=0; i<number; i++){
            l.append(words[i] + " ");
        }
        return l.toString();
    }


